import React from "react";
import CharacterItem from "./CharacterItem";
import Spinner from "../ui/Spinner";

const CharacterGrid = ({
  name,
  image,
  status,
  gender,
  id,
  species,
  isLoading,
}) => {
  return isLoading ? (
    <Spinner />
  ) : (
    <section className="character-list">
      {items.map(({ name, image, status, gender, id, species }) => {
        return (
          <CharacterItem
            key={id}
            name={name}
            image={image}
            status={status}
            gender={gender}
            species={species}
          />
        );
      })}
    </section>
  );
};

export default CharacterGrid;
